-author("Novakov").

-mode(compile).
%% API
-export([main/1]).

solve([C1 | [C2 | [C3 | [C4 | [C5 | Rest]]]]], Max) ->
        N1 = C1 - 48,
        N2 = C2 - 48,
        N3 = C3 - 48,
        N4 = C4 - 48,
        N5 = C5 - 48,

        Product = N1 * N2 * N3 * N4 * N5,
        solve([C2, C3, C4, C5] ++ Rest, max(Max, Product));

solve(_, Max) ->
        Max.


solve(Str) ->
        solve(Str, 0).

main(_) ->
        BigNum = "",
%%         io:format("~p\n", [solve("1234567890")])
         io:format("~p\n", [solve(BigNum)])
.
